import React from 'react';
import ReactDOM from 'react-dom';

class ClickButton extends React.Component {
  constructor() {
    super();
    this.state = {
      clickNum: 0
    };
    // This binding is necessary to make `this` work in the onclick callback
    this.handleClick = this.handleClicks.bind(this);
  }
  handleClicks() {
    // this.setState({
    // 	clicked: this.state.clicked + 1
    // });

    // Above may be easier to understand for beginners,
    // but the below is the correct way to do this as setState is async
    this.setState(prevState => ({
      clickNum: prevState.clicked + 1
    }));

  }
  render() {
    return (
      <div>
        <h1>The button is clicked {this.state.clickNum} times.</h1>
        <button type="button" onClick={this.handleClicks}>Click</button>
      </div>
    );
  }
}

ReactDOM.render(
  <Clicky/>,
  document.getElementById('root')
);
